export default function Example() {
  return (
    <>
      <section id="blogs">
        <div className=" bg-white py-24 sm:py-32 ">
          <div className="mx-auto max-w-7xl px-6 lg:px-8 " >
            <div className="mx-auto max-w-2xl lg:mx-0 ">
              <h2 className="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl ">Our Blogs</h2>
              <p className="mt-2 text-lg leading-8 text-gray-600">
                "Yoga is the journey of the self, through the self, to the self." - The Bhagavad Gita
              </p>
            </div>
            <div className="mx-auto mt-10 grid max-w-2xl grid-cols-1 gap-x-8 gap-y-16 border-t border-gray-200 pt-10 sm:mt-16 sm:pt-16 lg:mx-0 lg:max-w-none lg:grid-cols-3 h-[400px] ">
              {posts.map((post) => (
                <article key={post.id} className=" flex max-w-xl flex-col items-start justify-between max-w-sm rounded overflow-hidden shadow-lg">
                  <div className="flex items-center gap-x-4 text-xs ">
                    <time dateTime={post.datetime} className="text-gray-500 ">
                      {post.date}
                    </time>
                  </div>
                  <div className="group relative">
                    <h3 className="mt-3 text-lg font-semibold leading-6 text-gray-900 group-hover:text-gray-600 ">
                      <a href={post.href}>
                        <span className="absolute inset-0" />
                        {post.title}
                      </a>
                    </h3>
                    <p className="mt-5 line-clamp-3 text-sm leading-6 text-gray-600 ">{post.description}</p>
                  </div>
                  <div className="relative mt-8 flex items-center gap-x-4">
                    <img src={post.author.imageUrl} alt="" className="h-10 w-10 rounded-full bg-gray-50" />
                    <div className="text-sm leading-6">
                      <p className="font-semibold text-gray-900">
                        <a href={post.author.href}>
                          <span className="absolute inset-0" />
                          {post.author.name}
                        </a>
                      </p>
                      <p className="text-gray-600">{post.author.role}</p>
                    </div>
                  </div>
                </article>
              ))}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
